// Function: Build and send a Modbus RTU request

// CRC16 (Modbus) calculation function
function calculateCRC(buffer) {
    let crc = 0xFFFF;
    for (let pos = 0; pos < buffer.length; pos++) {
        crc ^= buffer[pos];
        for (let i = 0; i < 8; i++) {
            if (crc & 0x0001) {
                crc = (crc >> 1) ^ 0xA001;
            } else {
                crc = crc >> 1;
            }
        }
    }
    return crc;
}

// Define Modbus parameters
const slaveId = 0x01;           // Modbus slave ID
const functionCode = 0x03;      // Read Holding Registers
const startRegister = 0x0000;   // Starting register address
const registerCount = 3;        // Number of registers to read

// Create a Buffer for Slave ID (1), Function Code (1), Start Register (2), and Count (2)
let reqBuf = Buffer.alloc(6);
reqBuf.writeUInt8(slaveId, 0);
reqBuf.writeUInt8(functionCode, 1);
reqBuf.writeUInt16BE(startRegister, 2);
reqBuf.writeUInt16BE(registerCount, 4);

// Calculate the CRC and append (CRC is 2 bytes, little-endian)
const crc = calculateCRC(reqBuf);
let crcBuf = Buffer.alloc(2);
crcBuf.writeUInt16LE(crc, 0);

// Combine the request buffer and the CRC buffer
const packet = Buffer.concat([reqBuf, crcBuf]);

// Set the packet as the payload to be sent out via the serial out node
msg.payload = packet;
return msg;

// Function: Process the Modbus RTU response

// CRC16 (Modbus) calculation function
function calculateCRC(buffer) {
    let crc = 0xFFFF;
    for (let pos = 0; pos < buffer.length; pos++) {
        crc ^= buffer[pos];
        for (let i = 0; i < 8; i++) {
            if (crc & 0x0001) {
                crc = (crc >> 1) ^ 0xA001;
            } else {
                crc = crc >> 1;
            }
        }
    }
    return crc;
}

// If msg.payload is a string, convert it to a Buffer.
let buf = msg.payload;
if (typeof buf === "string") {
    buf = Buffer.from(buf, "binary");
}

// Ensure the response is long enough (minimum expected length: address(1) + function(1) + byte count(1) + data (2 bytes * count) + CRC(2))
if (buf.length < 7) {
    node.error("Incomplete response.", msg);
    return null;
}

// The third byte holds the number of data bytes returned.
const byteCount = buf[2];

// Check if the full message is present: header + data bytes + CRC
if (buf.length < 3 + byteCount + 2) {
    node.error("Incomplete response data.", msg);
    return null;
}

// Extract the data portion and the received CRC
const dataBuf = buf.slice(3, 3 + byteCount);
const receivedCrc = buf.readUInt16LE(buf.length - 2);

// Calculate the CRC on all bytes except the last two (the CRC itself)
const calculatedCrc = calculateCRC(buf.slice(0, buf.length - 2));

if (calculatedCrc !== receivedCrc) {
    node.error(
        "CRC mismatch. Calculated: " +
        calculatedCrc.toString(16) +
        ", Received: " +
        receivedCrc.toString(16),
        msg
    );
    return null;
}

// Decode registers: each register is 2 bytes in big-endian order.
let registers = [];
for (let i = 0; i < dataBuf.length; i += 2) {
    registers.push(dataBuf.readUInt16BE(i));
}

if (registers.length < 3) {
    node.error("Not enough registers returned.", msg);
    return null;
}

// Interpret the registers:
// - First register: instantaneous flow (divide by 10 for scaling)
// - Next two registers: cumulative flow (combine high and low registers, then scale)
const instantaneous_flow = registers[0] / 10.0;
const cumulative_flow = ((registers[1] << 16) | registers[2]) / 10.0;

// Output a JSON object with the decoded values
msg.payload = {
    instantaneous_flow: instantaneous_flow,
    cumulative_flow: cumulative_flow
};

// Convert to string explicitly if desired
msg.payload = JSON.stringify(msg.payload);


return msg;

